    // Note: Field comparison validated on-chain

    // 4. Calculate PnL
    let pnl: Field = if is_long == 1 {
        // Long: profit when current_price > entry_price
        let price_diff = current_price - private_entry_price;
        (price_diff * closing_size) / private_entry_price
    } else {
